一、Bubble 冒泡排序

[javascript]
var bubbleSort = function(arr) {
	for(var i = 0; i &lt; arr.length-1; i++) {
		for(var j = 0; j &lt; arr.length-i-1; j++) {
                        if(arr[j] &gt; arr[j+1]) {
				var temp = arr[j];
				arr[j] = arr[j + 1];
				arr[j + 1] = temp;
			}
		}
	}
	return arr;
}
[/javascript]

